INSERT INTO `test` (`id_t`, `question`, `choice1`, `choice2`, `choice3`, `choice4`, `id_l`) VALUES
(2, 'สงครามเกาหลีและสงครามเวียดนามมีลักษณะคล้ายคลึงกันอย่างไร ', 'ก.สงครามตัวแทน เพื่อการถ่ายทอดแนวคิดจากโลกตะวันตก ', 'ข.สงครามที่อเมริกาเข้ามาส่วนเกี่ยวข้อง และได้รับความพ่ายแพ้ ', 'ค.สงครามภายในประเทศ ที่เกิดจากความขัดแย้งด้านพรมแดน ', 'ง.สงครามที่ไม่ใช้อาวุธในการสงคราม หากแต่สงครามจิตวิทยา ', 4),
(3, 'การเกิดภาวะเงินฝืด มาตรการในข้อใดไม่สามารถนำมาแก้ไขปัญหาทางเศรษฐกิจได้', 'ตัวเลือกที่ 1 : ธนาคารกลางลดอัตราดอกเบี้ย                                                                                                            ', 'ตัวเลือกที่ 2 : รัฐบาลกลางลดการขยายพันธบัตร                                                                                                            ', 'ตัวเลือกที่ 3 : รัฐบาลลดอัตราภาษีมูลค่าเพิ่ม                                                                                                            ', 'ตัวเลือกที่ 4 : ธนาคารพาณิชย์ลดการปล่อยสินเชื่อ                                                                                                            ', 1),
(4, 'ข้อใดเป็นภาษีทางตรง', 'ภาษีเงินได้ปิโตรเลียม', 'ภาษีสรรพสามิต', 'ภาษีศุลกากร', 'ภาษีการค้า', 1),
(5, 'ผลกระทบที่เกิดจากรัฐบาลประกาศเพิ่มค่าแรงขั้นต่ำทั้งประเทศ', 'การจ้างงานเพิ่มขึ้น', 'เกิดอุปทานแรงงานส่วนเกิน', 'โรงงานสามารถลดต้นทุนการผลิตได้', 'เกิดอุปสงค์แรงงานส่วนเกิน', 1),
(6, 'ทฤษฎีเกษตรผสมผสานหรือทฤษฎีใหม่ในพระบาทสมเด็จพระเจ้าอยู่หัว รัชกาลที่ 9 มีจุดเริ่มต้นที่ใด', 'โครงการสถานีบนดอยอ่างขาง จังหวัดเชียงใหม่', 'โครงการแปลงนาสาธิต ในเขตพระตำหนักจิตรลดารโหฐาน', 'วัดมงคลชัยพัฒนา จังหวัดสระบุรี', 'สถานีเกษตรหุบกะพง จังหวัดเพชรบุรี', 1),
(7, 'ข้อใดคือชื่อของตัวอักษรของชาวเมโสโปเตเมีย', 'ซิกกูแรต', 'ไทกรีสยูเฟรติส', 'คูนิฟอร์ม', 'เฮียโรกลีฟิค', 1),
(8, 'ตั้งแต่ พ.ศ. 2475 เป็นต้นมา ประเทศไทยมีการยุบสภาผู้แทนราษฎรบ่อยครั้ง  ข้อใดคือเหตุปัจจัยสำคัญ ', 'ความไม่พร้อมของประชาชนต่อระบบสภาผู้แทนราษฎร', 'ความขาดเสถียรภาพของสภาผู้แทนราษฎร', 'ความด้อยประสิทธิ์ภาพของสภาผู้แทนราษฎร', 'ถูกทุกข้อ', 2),
(9, 'ข้อใดแสดงการเรียงลำดับชั้นของกฎหมายได้ถูกต้อง ', 'รัฐธรรมนูญ พระราชบัญญัติ พระราชกฤษฎีกา กฎกระทรวง', 'พระราชบัญญัติ พระราชกฤษฎีกา กฎกระทรวง เทศบัญญัติ', 'รัฐธรรมนูญ พระราชกำหนด พระราชบัญญัติ', 'พระราชบัญญัติ พระราชกำหนด พระราชกฤษฎีกา', 2),
(10, 'ทรัพย์สินทางปัญญาประเภทใด ไม่ต้องจดทะเบียนต่อเจ้าพนักงานเพื่อคุ้มครองสิทธิ์ ', 'หนังสือ', 'ภาพยนตร์', 'ละคร', 'ถูกทุกข้อ', 2),
(11, 'ข้อใดจัดเป็นความผิดลหุโทษ ', 'ผู้ใดพกอาวุธไปในเมือง หมู่บ้าน หรือทางสาธารณะโดยเปิดเผยหรือโดยไม่มีเหตุอันควร', 'ผู้ใดเห็นผู้อื่นตกอยู่ในภัยอันตรายแห่งชีวิต ซึ่งตนอาจช่วยได้แต่ไม่ช่วยตามความจำเป็น', 'ผู้ใดกระทำโดยประมาทและการกระทำนั้นเป็นเหตุให้ผู้อื่นได้รับอันตรายแก่กายหรือจิตใจ', 'ถูกทุกข้อ', 2),
(12, 'ข้อใดเรียงลำดับการปฏิวัติได้ถูกต้อง ', 'ปฏิวัติอเมริกา ปฏิวัติรัสเซีย ปฏิวัติอังกฤษ', 'ปฏิวัติรัสเซีย ปฏิวัติอเมริกา ปฏิวัติประเทศไทย', 'ปฏิวัติอเมริกา ปฏวัติฝรั่งเศส ปฏิวัติรัสเซีย', 'ปฏิวัติอังกฤษ ปฏิวัติอุตสาหกรรม ปฏวัติประเทศไทย', 2),
(13, 'ปฏิวัติดอกมะลิ คือการปฏิวัติของประเทศใด', 'ประเทศไทย', 'ประเทศตูนีเซีย', 'ประเทศอิหร่าน', 'ประเทศเกาหลีเหนือ', 2),
(14, 'ผู้ว่าราชการกรุงเทพมหานคร จัดอยู่ในรูปแบบการปกครองแบบใด', 'รวมอำนาจ', 'แบ่งอำนาจ', 'กระจายอำนาจ', 'กระทรวง', 2),
(15, 'ประเทศติมอร์-เลสเต แยกมาจากประเทศใดในอดีต', 'ประเทศอินโดนีเซีย', 'ประเทศมาเลเซีย', 'ประเทศสิงคโปร์', 'ประเทศฟิลิปปินส์', 2),
(16, 'ข้อใดกล่าวได้ถูกต้อง', 'การสมรสของชายและหญิงอายุต่ำกว่า 17 ปี  ให้ถือเป็นโมฆะ', 'การหมั้นของชายและหญิงหากมีฝ่ายใดก็ตามอายุต่ำกว่า 17 ปีให้ถือว่าสมบูรณ์', 'ผู้เยาว์คือบุคคลที่อายุไม่ถึง 20 ปี', 'บุคคลอายุต่ำกว่า 18 ปี ทำพินัยกรรมให้ถือว่าพินัยกรรมเป็นโมฆียะ', 2),
(17, 'การกู้ยืมเงินจำนวนเท่าใด หากไม่มีหลักฐานเป็นหนังสือจะฟ้องร้องบังคับคดีไม่ได้', '1,999 บาท', '2,000 บาท', '2,001 บาท', 'ไม่มีข้อใดถูกต้อง', 2),
(18, 'พระอนุรุทธะเมื่อบวชเป็นพระสงฆ์แล้วได้เรียนพระกรรมฐานในสำนักของใคร', 'พระอานนท์', 'พระสารีบุตร', 'พระโมคคัลลานะ', 'พระอัญญาโกณฑัญญะ', 23),
(19, 'ข้อใด ไม่ใช่ คุณธรรมที่ควรยึดถือเป็นแบบอย่างของพระอนุรุทธะ', 'ตั้งใจจริง', 'มีวาทศิลป์ในการพูด', 'มีความเพียรพยายาม', 'มีความกตัญญูกตเวที', 23),
(20, 'พระอนุรุทธะได้รับยกย่องจากพระพุทธเจ้าว่ายอดเยี่ยมกว่าพระภิกษุทั้งหลายในด้านใด', 'มีปัญญามาก', 'มีบริวารมาก', 'มีทิพพจักขุญาณ', 'มีความกตัญญูกตเวที', 23),
